let toggleBtn=document.getElementById('menu-toggle'),menu=document.getElementById('menu-main');document.addEventListener('DOMContentLoaded',function(){toggleBtn.addEventListener('click',()=>{let a=toggleBtn.getAttribute("aria-expanded")=="true"?"false":"true";toggleBtn.setAttribute("aria-expanded",a),document.body.classList.toggle("top-0"),document.body.classList.toggle("fixed")})}),window.addEventListener('resize',function(){window.innerWidth>=768&&toggleBtn.setAttribute("aria-expanded","false")})